  describe("blockify:", () => {
    it("should work for one block", () => {
      const blocks = blockify("<h3>Hello world</h3>");
      expect(blocks.length).to.equal(1);
      expect(blocks[0].type).to.equal(BLOCK_TYPES.H3);
      expect(blocks[0].data.value).to.equal("Hello world");
    });

    it("should trim space", () => {
      const blocks = blockify("<h3>  Hello world</h3>");
      expect(blocks.length).to.equal(1);
      expect(blocks[0].type).to.equal(BLOCK_TYPES.H3);
      expect(blocks[0].data.value).to.equal("Hello world");
    });

    it("should work for lists", () => {
      const blocks = blockify("<ul><li>Hello</li><li>world</li></ul>");
      expect(blocks.length).to.equal(2);
      expect(blocks[0].type).to.equal(BLOCK_TYPES.UnorderedItem);
      expect(blocks[0].data.value).to.equal("Hello");
    });

    it("should work for a mix of blocks", () => {
      const blocks = blockify("<p><i>Hey</i></p><ul><li>Hello</li><li>world</li></ul><ol><li>First</li></ol>");

      expect(blocks.length).to.equal(4);
      expect(blocks[0].type).to.equal(BLOCK_TYPES.Paragraph);
      expect(blocks[3].type).to.equal(BLOCK_TYPES.OrderedItem);
      expect(blocks[0].data.value).to.equal("<i>Hey</i>");
    });

    it("should work html cleaned from word", () => {
      const blocks = blockify(`
        <p><b>Crasmattis</b> consectetur <i>purus</i> sit </p>
        <ul><li>amet fermentum</li></ul>
        <ul><li>askdlaskjk</li></ul>`
      );

      expect(blocks.length).to.equal(3);
      expect(blocks[0].type).to.equal(BLOCK_TYPES.Paragraph);
      expect(blocks[1].type).to.equal(BLOCK_TYPES.UnorderedItem);
      expect(blocks[2].type).to.equal(BLOCK_TYPES.UnorderedItem);
      expect(blocks[1].data.value).to.equal("amet fermentum");
    });
  });

  describe("pasteHTML:", () => {
    const inlineHTML = "<span>Hello <i>world</i></span>";
    const blockHTML = "<p>A paragraph</p>";
    const multipleBlockHTML = "<p>A paragraph</p><p>Another paragraph</p>";

    it("should return null for pasting inline html on h1", () => {
      const result = pasteHTML(inlineHTML, "h1", "", "");
      expect(result).to.equal(null);
    });

    it("should return null for pasting block html on h1", () => {
      const result = pasteHTML(blockHTML, "h1", "", "");
      expect(result).to.equal(null);
    });

    it("should return null for pasting inline html on p", () => {
      const result = pasteHTML(inlineHTML, "p", "", "");
      expect(result).to.equal(null);
    });

    it("should paste in the current block if tags matches and current html is empty", () => {
      const { blocks, replaceCurrent } = pasteHTML(blockHTML, "p", "", "");
      expect(replaceCurrent).to.equal(true);
      expect(blocks.length).to.equal(1);
      expect(blocks[0].type).to.equal(BLOCK_TYPES.Paragraph);
      expect(blocks[0].data.value).to.equal("A paragraph");
    });

    it("should paste in the current block for a p and if current html is empty (multiblocks)", () => {
      const { blocks, replaceCurrent } = pasteHTML(multipleBlockHTML, "p", "", "");
      expect(replaceCurrent).to.equal(true);
      expect(blocks.length).to.equal(2);
      expect(blocks[0].type).to.equal(BLOCK_TYPES.Paragraph);
      expect(blocks[0].data.value).to.equal("A paragraph");
      expect(blocks[1].type).to.equal(BLOCK_TYPES.Paragraph);
      expect(blocks[1].data.value).to.equal("Another paragraph");
    });

    it("should paste in the next block for a p and if current html is not empty", () => {
      const { blocks, replaceCurrent } = pasteHTML(blockHTML, "p", "Hey", "");
      expect(replaceCurrent).to.equal(false);
      expect(blocks.length).to.equal(1);
      expect(blocks[0].type).to.equal(BLOCK_TYPES.Paragraph);
      expect(blocks[0].data.value).to.equal("A paragraph");
    });

    it("should paste in the next block for a h3 and if current html is empty", () => {
      const { blocks, replaceCurrent } = pasteHTML(blockHTML, "h3", "", "");
      expect(replaceCurrent).to.equal(false);
      expect(blocks.length).to.equal(1);
      expect(blocks[0].type).to.equal(BLOCK_TYPES.Paragraph);
      expect(blocks[0].data.value).to.equal("A paragraph");
    });

    it("should paste in the next block for a p and create a new block if pasting in the middle", () => {
      const { blocks, replaceCurrent } = pasteHTML(blockHTML, "p", "Hey", " what's up?");
      expect(replaceCurrent).to.equal(false);
      expect(blocks.length).to.equal(2);
      expect(blocks[0].type).to.equal(BLOCK_TYPES.Paragraph);
      expect(blocks[0].data.value).to.equal("A paragraph");
      expect(blocks[1].type).to.equal(BLOCK_TYPES.Paragraph);
      expect(blocks[1].data.value).to.equal(" what's up?");
    });

    it("should paste in the next block for a p and create a new block if pasting in the middle (multiblocks)", () => {
      const { blocks, replaceCurrent } = pasteHTML(multipleBlockHTML, "p", "Hey", " what's up?");
      expect(replaceCurrent).to.equal(false);
      expect(blocks.length).to.equal(3);
      expect(blocks[0].type).to.equal(BLOCK_TYPES.Paragraph);
      expect(blocks[0].data.value).to.equal("A paragraph");
      expect(blocks[1].type).to.equal(BLOCK_TYPES.Paragraph);
      expect(blocks[1].data.value).to.equal("Another paragraph");
      expect(blocks[2].type).to.equal(BLOCK_TYPES.Paragraph);
      expect(blocks[2].data.value).to.equal(" what's up?");
    });
  });
